import UIKit

// MARK: - TileSet

struct TileSet: Codable {
  var tiles: [Tile]
  private let maxLuminosity: CGFloat
  func tile(for brightness: CGFloat) -> Tile {
    tiles.min { ($0.brightness - brightness).magnitude < ($1.brightness - brightness).magnitude } ?? tiles[0]
  }
  func tile(r: UInt8, g: UInt8, b: UInt8) -> Tile {
    let deltas = tiles.map { tile -> Int in
      let dr = Int(r) - Int(tile.r)
      let db = Int(g) - Int(tile.g)
      let dg = Int(b) - Int(tile.b)
      return dr*dr + db*db + dg*dg
    }
    let index = deltas.enumerated().min(by: {
      $0.element < $1.element
    })?.offset ?? 0
    return tiles[index]
  }
  func calculateSize(points: CGFloat) -> CGSize {
    guard let firstTile = tiles.first,
      let resized = Tile(symbol: firstTile.symbol, size: points) else {
      return .zero
    }
    return resized.size
  }
}

// MARK: - TileSet Factory Methods

extension TileSet {
  static func makeAscii(points: CGFloat) -> TileSet {
    let exclusions: Set<Int> = [34, 39, 92]
    var tiles = (32..<126)
      .filter {!exclusions.contains($0)}
      .compactMap { Tile(value: $0, size: points) }
    let maxLuminosity = tiles.max { $0.brightness < $1.brightness}?.brightness ?? 0
    tiles.indices.forEach { tiles[$0].brightness /= maxLuminosity }
    return .init(tiles: tiles, maxLuminosity: maxLuminosity)
  }
